        for(let i=startIndex;i= curPath[curPath.length-1]) && !usedSet.has(nums[i])) {
                curPath.push(nums[i]);
                backtracking(i+1, nums, curPath);
                curPath.pop();
            }
            usedSet.add(nums[i]);
        }
    };
    backtracking(0, nums, []);
    return result;
};

